@use "../abstracts/typography.scss";
@use "../abstracts/colors";
@use "../abstracts/mixins";

a {
  color: white;
  text-decoration: none;
  &:hover {
    color: colors.$black;
  }
}
.container {
  display: flex;
  justify-content: flex-end;
  align-items: flex-end;
  position: absolute;
  width: 8em;
  height: 2.5em;
  margin: 20px;
  top: 60vh;
  // perspective: 1000;
  // -webkit-perspective: 1000;
  // backface-visibility: hidden;
  // -webkit-backface-visibility: hidden;
  // background: #fff;
}

.pulse-button {
  transition: ease-in-out;
  position: absolute;
  width: 8em;
  height: 2.5em;
  font-size: 1em;
  padding: 2px;
  text-align: center;
  vertical-align: middle;
  line-height: 2.5em;
  // letter-spacing: -1px;
  color: white;
  border: none;
  border-radius: 2em;
  background: linear-gradient(91.08deg, #00b898 -16.5%, rgba(3, 3, 3, 0) 200%);
  cursor: pointer;
  box-shadow: 0 0 0 0 rgba(#00b898, 0.5);
  animation: pulse 2.5s infinite;
  -webkit-animation: pulse 2.5s infinite;
}
.pulse-button:hover {
  animation: none;
  -webkit-animation: none;
}

// uncomment after development
@keyframes pulse {
  0% {
    transform: scale(90%);
  }
  50% {
    transform: scale(100%);
    box-shadow: 0 0 0 1.25rem rgba(#9bdede, 0);
  }
  100% {
    transform: scale(90%);
    box-shadow: 0 0 0 0 rgba(#9bdede, 40%);
  }
}

#contactBTN {
  font-size: 1.15rem;
  border-radius: 1rem;
  padding: 0.35rem 2.2rem;
  color: colors.$bBlue;
  border-color: colors.$litePrim;
  background-color: colors.$bgClr;
  &:hover {
    cursor: pointer;
    background-color: colors.$drkrPrim;
    transition: all ease-in-out 250ms;
    transform: scale(1.2);
  }
}

