@use "../abstracts/colors";
@use "../abstracts/typography.scss";
@use "../abstracts/mixins";

// #secondary {

// }
nav {
  margin: 0;
  height: 12vh;
  background-color: colors.$bgClr;
  box-shadow: 0 0 4px 2px rgba(#00b898, 0.2);
  // border: 3px solid red;
}
#navigation {
  display: flex;
  justify-content: space-between;
  align-items: center;
  align-self: flex-start;
  position: sticky;
  top: 0;
  // bottom: 0;
  z-index: 10;
  padding: 1em;
  overflow-y: unset;
}
.menuItems {
  display: flex;
  list-style: none;
  padding: 0;
  margin: 0;
  // width: 100vw;
  // height: 100vh; /// ----> to make full height of nav element//
  // background-color: red;
  justify-content: flex-end;
  align-items: center;
  // margin-left: auto;
  & li a {
    color: colors.$white;
    text-decoration: none;
    font-size: 1.25em;
    padding: 1em;
  }
  & :hover {
    cursor: pointer;
    color: colors.$bBlue;
  }
}
@include mixins.screen("mobile") {
  .line {
    position: absolute;
    width: 30px;
    height: 3px;
    background: white;
    z-index: 4;
    margin: 5px;
    border-radius: 20%;
    transition: all cubic-bezier(0.5, 0.1, 1, 1) 0.25s;
  }
  .top {
    top: 20%;
  }
  .middle {
    top: 45%;
  }
  .bottom {
    top: 70%;
  }
  .icon.close .top {
    transform: rotate(45deg);
    top: 30%;
  }
  .icon.close .middle,
  .icon.close .bottom {
    transform: rotate(-45deg);
    top: 30%;
  }
  nav {
    position: relative;
  }
  .hamburger {
    position: absolute;
    cursor: pointer;
    right: 0;
    top: 50%;
    // margin: 0px 13px;
    // margin-right: auto;
    // margin-top: 5px;
    transform: translate(-5%, -50%);
    // padding: 5px;
    height: 3rem;
    width: 5rem;
    display: grid;
    justify-content: center;
    justify-items: center;
    z-index: 2;
  }
  .menuItems {
    display: none;
    transition: all ease;
    position: relative;
    pointer-events: none; //-->used for viewing the burger before commenting it out
    width: 100vw;
    height: 100vh;
    background-color: colors.$bgClr;
    flex-direction: column;
    justify-content: space-evenly;
  }
  .menuItems.open {
    // display: block;
    position: absolute;
    top: 0;
    left: 0;
    display: flex;
    justify-content: space-evenly;
    align-items: center;
    pointer-events: all;
  }
}

#logo img {
  position: absolute;
  top: 0;
  left: -1%;
  height: 100%;
  width: 35%;
  object-fit: fill;
}

.menuItems :after {
  transition: all 0.5s;
}
.hover {
  position: relative;
}
.hover:after {
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  margin: auto;
  width: 0%;
  content: ".";
  color: transparent;
  background: colors.$litePrim;
  height: 1px;
}
.hover:hover:after {
  width: 100%;
}

