@use "../abstracts/typography.scss";
@use "../abstracts/colors";
@use "../abstracts/mixins";

.jhead {
  display: flex;
  justify-content: center;
  align-items: center;
  text-align: center;
  padding: 2vmax;
  font-size: 2rem;
  font-family: "Rokkit";
  background-color: colors.$bgOpacClr;
  @include mixins.screen("mobile") {
    font-size: 1rem;
    line-height: 30px;
    text-align: center;
  }
}

// #timeline {
//   // margin: 10vmin;
// }
#progress {
  background: colors.$bgOpacClr;
}
::selection {
  color: #fff;
  background: colors.$litePrim;
}

section p {
  line-height: 1.5rem;
  text-align: left;
}

.wrapper {
  max-width: 100vw;
  min-height: 100vh;
  // margin: 100px auto;
  padding: 2.5em 8em;
  position: relative;
  background: colors.$bgOpacClr;
}
.wrapper .center-line {
  position: absolute;
  border-radius: 1rem;
  height: 96.5%;
  width: 4px;
  background: colors.$drkrPrim;
  left: 50%;
  top: 20px;
  transform: translateX(-50%);
  @include mixins.screen("tablet") {
    // display: none;
    transform: translateX(101px);
  }
  @include mixins.screen("mobile") {
    display: none;
  }
}
.wrapper .row {
  display: flex;
}
.wrapper .row-1 {
  justify-content: flex-start;
  @include mixins.screen("mobile") {
    justify-content: center;
  }
}
.wrapper .row-2 {
  justify-content: flex-end;
  @include mixins.screen("mobile") {
    justify-content: center;
  }
}
.wrapper .row section {
  font-family: "Raleway";
  font-weight: 400px;
  background: colors.$timeEv;
  border-radius: 15px;
  width: calc(50% - 45px);
  height: auto;
  min-height: 17em;
  min-width: 300px;
  padding: 20px;
  position: relative;
  @include mixins.screen("mobile") {
    width: 400px;
    height: auto;
  }
  @include mixins.screen("tablet") {
    width: 500px;
    height: auto;
  }
  @media (max-width: 1015px) and (min-width: 810px) {
    width: 500px;
    height: auto;
  }
}
.wrapper .row section::before {
  position: absolute;
  content: "";
  height: 15px;
  width: 15px;
  background: colors.$timeEv;
  top: 28px;
  // z-index: 1;
  transform: rotate(45deg);
}
.row-1 section::before {
  right: -7px;
}
.row-2 section::before {
  left: -7px;
}
.row section .ball,
.center-line .scroll-icon {
  position: absolute;
  // left: 106%;
  background: colors.$litePrim;
  height: 30px;
  width: 30px;
  text-align: center;
  line-height: 40px;
  border-radius: 50%;
  // color: #ff7979;
  font-size: 17px;
  box-shadow: 0px 0px 2em colors.$bBlue;
  @include mixins.screen("tablet") {
    // display: none;
  }
  @include mixins.screen("mobile") {
    display: none;
  }
}
// @media screen and (max-width: 992px) {
//   .ball {
//     background-color: blue;
//   }
// }
.center-line .scroll-icon {
  bottom: 0px;
  left: 50%;
  font-size: 25px;
  transform: translateX(-50%);
}
.row-1 section .ball {
  top: 15px;
  right: -60px;
}
.row-2 section .ball {
  top: 15px;
  left: -60px;
}
.row section .details,
.row section .bottom {
  display: flex;
  align-items: center;
  justify-content: space-between;
}
.row section .details .title {
  font-size: 22px;
  font-weight: 600;
}
.row section p {
  margin: 10px 0 17px 0;
}
// .row section .bottom a {
//   text-decoration: none;
//   color: #fff;
//   padding: 7px 15px;
//   border-radius: 15px;
//   /* font-size: 17px; */
//   font-weight: 400;
//   transition: all 0.3s ease;
// }
// .row section .bottom a:hover {
//   transform: scale(0.97);
// }

@media (max-width: 1020px) and (min-width: 791px) {
  .wrapper .center-line {
    left: 40px;
  }
  .wrapper .row {
    margin: 30px 0 3px 60px;
  }
  .wrapper .row section {
    width: 100%;
  }
  .row-1 section::before {
    left: -7px;
  }
  .row-1 section .ball {
    left: -60px;
  }
}
@media (max-width: 790px) {
  .wrapper .center-line {
    left: 40px;
  }
  .wrapper .row {
    margin: 30px 0 3px 60px;
  }
  .wrapper .row section {
    width: 100%;
  }
  .row-1 section::before {
    left: -7px;
  }
  .row-1 section .ball {
    left: -60px;
  }
}
@media (max-width: 440px) {
  .wrapper .center-line,
  .row section::before,
  .row section .ball {
    display: none;
  }
  .wrapper .row {
    margin: 10px 0;
  }
}

