@use '../abstracts/colors';
@use '../abstracts/typography.scss';
@use '../abstracts/mixins';

.jhead {
  font-size: 2rem;
  @include mixins.screen("mobile") {
    font-size: 1rem;
  }
}
.box {
  display: flex;
  justify-content: center;
  align-items: center;
  background: colors.$bgOpacClr;
}

.aboutMe {
  // background: colors.$bgOpacClr;
  position: relative;
  height: 100%;
  width: 100%;
}
.abt {
  width: 80%;
  text-align: center;
  min-height: 40vh;
  background: colors.$jungleG;
  margin-top: 50px;
  padding: 3em;
  padding-top: 5px !important;
  padding-bottom: 5px !important;
  color: colors.$white;
  line-height: 2.5rem;
  // border-radius: 1rem;
  justify-self: center;
  // box-shadow: colors.$bgClr 0px 5px 15px;
  @include mixins.screen("mobile") {
    width: 100%;
  }
  @include mixins.screen("tablet") {
    width: 95%;
  }
  & p {
    margin: 0;
  }
  & h2 {
    // text-align: center;
    margin: 5px;
    padding: 5px;
  }
}

#abtH2 {
  color: colors.$bBlue;
}
.socials {
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 2em;
  padding-bottom: 0 !important;
}
.social-icons a img {
  border-radius: 50%;
}
.social-icons {
  margin: 0.5em;
  // padding-top: 4em;
  & img {
    height: 3.25em;
    margin-left: 5px;
    margin-right: 5px;
    &:hover {
      box-shadow: 0 0 10px colors.$bBlue;
    }
  }
}

//This is testing form //

body,
input,
textarea {
  font-family: "Raleway", sans-serif;
}

.formCont {
  position: relative;
  width: 100%;
  min-height: 100vh;
  padding: 2rem;
  background: colors.$bgOpacClr;
  overflow: hidden;
  display: flex;
  align-items: center;
  justify-content: center;
}

.form {
  width: 100%;
  max-width: 820px;
  background-color: black;
  border-radius: 10px;
  box-shadow: 0 0 10px 0px colors.$bBlue;
  z-index: 0;
  overflow: hidden;
  display: grid;
  grid-template-columns: repeat(2, 1fr);
}

.contact-form {
  background-color: colors.$jungleG;
  position: relative;
}

.circle {
  border-radius: 50%;
  background: linear-gradient(115deg, transparent 20%, colors.$drkrPrim);
  position: absolute;
}

.circle.one {
  width: 130px;
  height: 130px;
  top: 130px;
  right: -40px;
}

.circle.two {
  width: 80px;
  height: 80px;
  top: 10px;
  right: 30px;
}

.contact-form:before {
  content: "";
  position: absolute;
  width: 26px;
  height: 26px;
  background-color: colors.$jungleG;
  transform: rotate(45deg);
  top: 50px;
  left: -13px;
}

form {
  padding: 2.3rem 2.2rem;
  z-index: 0;
  overflow: hidden;
  position: relative;
}

h3.ctctMe {
  color: colors.$bBlue;
  font-weight: 500;
  font-size: 1.5rem;
  line-height: 1;
  margin-bottom: 0.7rem;
}

.input-container {
  position: relative;
  margin: 1rem 0;
}

.input-container.success input {
  border-color: colors.$iconClr;
}
.input-container.error input {
  border-color: red;
}

.input-container.success i.fa-check-circle {
  visibility: visible;
  color: colors.$iconClr;
}
.input-container.error i.fa-exclamation-circle {
  visibility: visible;
  color: red;
}
.input-container.error small {
  visibility: visible;
  color: red;
}
.input-container i {
  visibility: hidden;
  position: absolute;
  top: 25%;
  right: 5%;
}
.input-container small {
  visibility: hidden;
  position: absolute;
  bottom: -5;
  left: 0;
}
.input {
  width: 100%;
  outline: none;
  border: 2px solid colors.$bgClr;
  background: none;
  padding: 0.6rem 1.2rem;
  color: colors.$bBlue;
  font-weight: 500;
  font-size: 0.95rem;
  letter-spacing: 0.5px;
  border-radius: 25px;
  transition: 0.3s;
}

textarea.input {
  padding: 0.8rem 1.2rem;
  min-height: 150px;
  border-radius: 22px;
  resize: none;
  overflow-y: auto;
}

.input-container label {
  position: absolute;
  top: 50%;
  left: 15px;
  transform: translateY(-50%);
  padding: 0 0.4rem;
  color: colors.$bBlue;
  font-size: 0.9rem;
  font-weight: 400;
  pointer-events: none;
  z-index: 0;
  transition: 0.5s;
}

.input-container.textarea label {
  top: 1rem;
  transform: translateY(0);
}

.btn {
  padding: 0.6rem 1.3rem;
  background: colors.$drkrPrim;
  border: 2px solid colors.$litePrim;
  font-size: 0.95rem;
  color: colors.$bBlue;
  line-height: 1;
  border-radius: 25px;
  outline: none;
  cursor: pointer;
  transition: 0.3s;
  margin: 0;
}

.btn:hover {
  background-color: transparent;
  // color: ;
}

.input-container span {
  position: absolute;
  top: 0;
  left: 25px;
  transform: translateY(-50%);
  font-size: 0.8rem;
  padding: 0 0.4rem;
  color: transparent;
  pointer-events: none;
  z-index: 5;
}

.input-container.focus label {
  top: 0;
  transform: translateY(-100%);
  left: 25px;
  font-size: 0.8rem;
}

.contact-info {
  padding: 2.3rem 2.2rem;
  position: relative;
  background: colors.$bgClr;
}

.contact-info .touch {
  color: #1abc9c;
  font-size: 1.5rem;
}

.text {
  color: white;
  margin: 1.5rem 0 2rem 0;
}

.information {
  display: flex;
  color: white;
  margin: 0.7rem 0;
  align-items: center;
  font-size: 0.95rem;
}

.icon {
  width: 28px;
  margin-right: 0.7rem;
}

.social-media {
  padding: 2rem 0 0 0;
}

.social-media p {
  color: white;
}

.square {
  position: absolute;
  height: 400px;
  top: 50%;
  left: 50%;
  transform: translate(181%, 11%);
  opacity: 0.2;
}

@media (max-width: 850px) {
  .form {
    grid-template-columns: 1fr;
  }

  .contact-form:before {
    top: -13px;
    left: initial;
    right: 70px;
  }

  .square {
    transform: translate(140%, 43%);
    height: 350px;
  }

  .text {
    margin: 1rem 0 1.5rem 0;
  }

  .social-media {
    padding: 1.5rem 0 0 0;
  }
}

@media (max-width: 480px) {
  .formCont {
    padding: 1.5rem;
  }

  .square {
    display: none;
  }

  form,
  .contact-info {
    padding: 1.7rem 1.6rem;
  }

  .text,
  .information,
  .social-media p {
    font-size: 0.8rem;
  }

  h3 .ctctMe {
    font-size: 1.15rem;
  }

  .social-icons a {
    width: 30px;
    height: 30px;
    line-height: 30px;
  }

  .icon {
    width: 23px;
  }

  .input {
    padding: 0.45rem 1.2rem;
  }

  .btn {
    padding: 0.45rem 1.2rem;
  }
}
