@use '../abstracts/colors';
@use '../abstracts/typography.scss';
@use '../abstracts/mixins';

#projects {
  background: colors.$bgOpacClr;
  position: relative;
  height: 100vh;
  width: 100%;
}

.pjHead {
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 5vmin;
  font-size: 2rem;
  // margin: 10vh;
}

// #secondary {
//   scroll-behavior: smooth;
//   transition: ease-in-out;
// }

.carousel {
  text-align: center;
  overflow: hidden;
  max-width: 60vw;
  position: absolute;
  left: 50%;
  top: 50%;
  transform: translate(-50%, -50%);
  margin-bottom: 5vmin;
}

.carousel .carousel__item,
.carousel .carousel__item--hidden {
  display: none;
}

.carousel .carousel__item img {
  width: 100%;
  max-width: 60vw;
  height: 60vh;
  max-height: 60vh;
  object-fit: contain;
}

.carousel .carousel__item--visible {
  display: block;
  animation: fadeVisibility 0.5s;
}

.carousel .carousel__actions {
  display: flex;
  width: 100%;
  justify-content: space-between;
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
}

.carousel .carousel__actions button {
  border-radius: 50px;
  border: 0;
  font-weight: bold;
  cursor: pointer;
  width: 40px;
  height: 40px;
}

.carousel .carousel__actions button#carousel__button--prev {
  margin-left: 20px;
}

.carousel .carousel__actions button#carousel__button--next {
  margin-right: 20px;
}

@keyframes fadeVisibility {
  0% {
    opacity: 0;
  }
  100% {
    opacity: 1;
    transform: opacity linear;
  }
}
